DROP DATABASE IF EXISTS gsb;
create database gsb;
use gsb;

drop table if exists `roles`;


CREATE TABLE `roles` (
                         `id_role` int (11) PRIMARY KEY auto_increment NOT NULL,
                         `nom_role` varchar(50) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

