drop table if exists utilisateurs;

CREATE TABLE `utilisateurs` (
`id_utilisateur` int(11) PRIMARY KEY auto_increment NOT NULL,
`nom` varchar(255) DEFAULT NULL,
`prenom` varchar(255) DEFAULT NULL,
`email` varchar(255) DEFAULT NULL,
`mot_de_passe` varchar(255) DEFAULT NULL,
`id_role` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;
ALTER TABLE `utilisateurs`

    ADD KEY `id_role_fk` (`id_role`);

drop table if exists commandes;

CREATE TABLE `commandes` (
                             `id_commande` int(11) PRIMARY KEY auto_increment NOT NULL,
                             `id_utilisateur` int(11) DEFAULT NULL,
                             `date_commande` datetime DEFAULT current_timestamp(),
                             `statut` enum('en_attente','validee','invalidée') DEFAULT 'en_attente',
                             `date_disponibilite` date DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

drop table if exists mouvements;

CREATE TABLE `mouvements` (
                              `id_mouvement` int(11) PRIMARY KEY auto_increment NOT NULL,
                              `id_med` int(11) DEFAULT NULL,
                              `type_mouvement` enum('entree','sortie','non catégorisé', 'demande') DEFAULT 'non catégorisé',
                              `quantite` int(11) ,
                              `date_mouvement` datetime DEFAULT current_timestamp(),
                              `id_commande` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

drop table if exists details_commande;

CREATE TABLE `details_commande` (
                                    `id_commande` int(11) NOT NULL,
                                    `id_med` int(11) NOT NULL,
                                    `quantite` int(11) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


drop table if exists medicaments ;

CREATE TABLE `medicaments` (
  `id_med` int(11) PRIMARY KEY auto_increment NOT NULL,
  `nom` varchar(255) DEFAULT NULL,
  `CIP` varchar(255) DEFAULT NULL,
  `type` varchar(200) DEFAULT NULL,
  `quantite_disponible` int(11) DEFAULT NULL,
  `prix` text
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;


ALTER TABLE `commandes`
    ADD KEY `id_utilisateur` (`id_utilisateur`);

ALTER TABLE `mouvements`
    ADD KEY `id_med` (`id_med`),
    ADD KEY `id_commande` (`id_commande`);

ALTER TABLE `commandes`
    ADD CONSTRAINT `commandes_ibfk_1` FOREIGN KEY (`id_utilisateur`) REFERENCES `utilisateurs` (`id_utilisateur`);

--
-- Contraintes pour la table `details_commande`
--
ALTER TABLE `details_commande`
    ADD CONSTRAINT `details_commande_ibfk_1` FOREIGN KEY (`id_commande`) REFERENCES `commandes` (`id_commande`),
    ADD CONSTRAINT `details_commande_ibfk_2` FOREIGN KEY (`id_med`) REFERENCES `medicaments` (`id_med`);

--
-- Contraintes pour la table `mouvements`
--
ALTER TABLE `mouvements`
    ADD CONSTRAINT `mouvements_ibfk_1` FOREIGN KEY (`id_med`) REFERENCES `medicaments` (`id_med`),
    ADD CONSTRAINT `mouvements_ibfk_2` FOREIGN KEY (`id_commande`) REFERENCES `commandes` (`id_commande`);

--
-- Contraintes pour la table `utilisateurs`
--
ALTER TABLE `utilisateurs`
    ADD CONSTRAINT `id_role_fk` FOREIGN KEY (`id_role`) REFERENCES `roles` (`id_role`);
